<?phpinclude "autoloader.php";include 'config/config.php';list($format , $title , $content) = [$_POST['format'],$_POST['title'] , $_POST['content']];if ($_SERVER['REQUEST_METHOD'] == 'POST'){    foreach ($Export_types as $export_type=>$export_class)    {        if ($format == $export_type)            (new $export_class(['title'=>$title , 'content'=>$content]))->export();    }  return ;}else{    echo "Error";}